// Get the focus decision from System Prompt Determinator
const focusDecision = $('Prompt Focus Determinator').first().json.output.focus;

// Get all the system prompts from the System Prompt node
const systemPrompts = $('Focus Prompts').first().json;

// Find the matching system prompt based on the focus decision
const selectedPrompt = systemPrompts[focusDecision];

// Replace all line breaks with \n to remove line spaces
const formattedPrompt = selectedPrompt ? selectedPrompt.replace(/\n/g, '\\n') : null;

// Return the result
return [
  {
    json: {
      focus: focusDecision,
      selected_prompt: formattedPrompt,
      // Optional: include all available prompts for debugging
      available_prompts: Object.keys(systemPrompts)
    }
  }
];

const items = $input.all();
const updatedItems = items.map((item) => {
  const base64Url = item?.json?.data;
  const url = `data:image/png;base64,${base64Url}`;
  return { url };
});
return updatedItems;

