// Function to clean problematic characters from text
function cleanText(text) {
  if (!text || typeof text !== 'string') {
    return text;
  }

  return text
    // Remove double periods
    .replace(/\.{2,}/g, '.')
    // Replace em dashes with regular dashes
    .replace(/—/g, '-')
    // Replace percentage symbols in problematic contexts
    .replace(/100%/g, '100 percent')
    // Fix common typos
    .replace(/PROVVIDED/g, 'PROVIDED')
    .replace(/Athmosphere/g, 'Atmosphere')
    // Clean up extra spaces after commas
    .replace(/,\s{2,}/g, ', ')
    // Remove problematic parenthetical caps phrases
    .replace(/\(FACE MUST 100% MATCH[^)]*\)/gi, '')
    // Soften absolute language
    .replace(/MUST 100% MATCH/gi, 'should closely match')
    .replace(/100% accuracy/gi, 'high accuracy')
    .replace(/absolute/gi, 'high')
    .replace(/zero differences/gi, 'minimal differences')
    .replace(/exactly the same/gi, 'very similar')
    // Clean up multiple spaces
    .replace(/\s{2,}/g, ' ')
    // Trim whitespace
    .trim();
}

// Process all input items
const cleanedItems = [];

for (const item of $input.all()) {
  const cleanedItem = {
    json: {
      'Focus': cleanText(item.json['Focus'] || item.json['Focus Prompt']), // Handle both field names
      'Core Subject': cleanText(item.json['Core Subject']),
      'Setting': cleanText(item.json['Setting']),
      'Composition': cleanText(item.json['Composition']),
      'Lighting': cleanText(item.json['Lighting']),
      'Style': cleanText(item.json['Style']),
      'Atmosphere': cleanText(item.json['Atmosphere']),
      'Color Palette': cleanText(item.json['Color Palette']),
      'Text Overlay': cleanText(item.json['Text Overlay']),
      'Video Prompt': cleanText(item.json['Video Prompt'])
    }
  };

  cleanedItems.push(cleanedItem);
}

return cleanedItems;

